{panel:bgColor=#eae6ff}
This is a recommended interface structure. It splits the necessary information in three groups: Pet info, Product and Feedings:

{noformat}import { Fraction } from 'fraction.js';

export interface FeedingGuide {
  pet: Pet;
  product: Product;
  feedings: Feeding[];
  transitions: Transition[];
}

export interface Pet {
  name: string;
  breed: string;
  weight: number;
  activityLevel: string;
  bodyCondition: string;
}

export interface Product {
  sku: string;
  family: string;
  selectionIndex: number;
  portionPercentage: number;
  isTrialPlan: boolean;
}

export interface Feeding {
  recipeType: 'dry' | 'fresh';
  portionShare: Fraction;
  getUnit(): 'cup' | 'packet' | undefined;
  // Get the portion share according to amount of times feeding happens per day
  getMultipleFeedingPerDay: (timesPerDay: number) => Fraction;
}

export interface Transition {
  daysRange: number[];
  percentagePetsTableFood: number;
}
{noformat}
